package exam_task;

public class evenodd10_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        int numbers;

        for (int number=1;number<=15;number++) {
            if (number % 2 == 0) {
                System.out.println(number + " is even ");
            } else {
                System.out.println(number + " is odd ");
            }
        }

	}

}
