package java_class_april;

public class data_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		

        // Primitive data types

        // boolean (true or false)
		
        boolean isTrue = true;
        System.out.println("boolean: " + isTrue);

        // char (single character)
        
        char initial = 'A';
        System.out.println("char: " + initial);

        // byte (8-bit signed integer)
        
        byte smallNumber = 127;
        System.out.println("byte: " + smallNumber);

        // short (16-bit signed integer)
        
        short mediumNumber = 32767;
        System.out.println("short: " + mediumNumber);

        // int (32-bit signed integer) - most commonly used for whole numbers
        
        int age = 25;
        System.out.println("int: " + age);
        
        // long (64-bit signed integer) - for larger whole numbers
        
        long largeNumber = 9999999999L; // Add 'L' for long literals
        System.out.println("long: " + largeNumber);
        
        // float (32-bit single-precision floating-point number) - for decimal values
        
        float pi = 3.14159f; // Add 'f' for float literals
        System.out.println("float: " + pi);

        // double (64-bit double-precision floating-point number) - more precise decimals
        
        double b = 6.02214085e23; // Scientific notation for large decimals
        System.out.println("double: "  + b);

        // Non-primitive data type (example)

        String name = "vijay";
        System.out.println("String : " + name);
    }	

	}


