package dowhile_01_05;

public class multipleby5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int m = 1;
        do {
            System.out.println("5 * " + m + " = " + (5 * m));
            m++;
        } while (m <= 10);

        
        
        //print numbers from 1 to 10 increment
        
        for (int j = 1; j <= 10; j++) {
            System.out.print(j + " ");
        }
        System.out.println(); 

        // print numbers from 10 to 1 decrement
        for (int k = 10; k >= 1; k--) {
            System.out.print(k + " ");
        }
    }

	



	}


