package java_task_april;

public class evenandodd_03_05_34 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int c=0,d=0;
		
		for(int i=1;i<=20;i++)
		{
			if(i%2==0)
			{
				c++;
			}
			else
			{
				d++;
			}
		}
	System.out.println("no of even numbers :"+c);
	System.out.println("no of odd numbers :"+d);
	}


	}
	


