package java_task_loops;

public class divisibleby2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int n = 40;
		
	       System.out.println("Numbers divisible by 2  in reverse order:");
	        
	       while (n >= 0) { 
	        	
	           if (n % 2 == 0) { 
	            	
	            System.out.print(n + " "); 
	            }
	                   n--;

	       }
	}

}
