package java_task_loops;

public class divisibleby3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		 int n = 15; 
		 
	        System.out.println("Numbers divisible by 3 in reverse order:");
	        
	        while (n > 1) { 
	        	
	            if (n % 3 == 0) { 
	            	
	                System.out.print(n + " "); 
	            }
	            n--;

	}

}
}