package java_task_loops;

public class divisibleby7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 30;
		
	       System.out.println("Numbers divisible by 7 in reverse order:");
	        
	       while (n >= 0) { 
	        	
	           if (n % 7 == 0) { 
	            	
	            System.out.print(n + " "); 
	            }
	                   n--;
	     }
	        
		
	    }

}		
			


